import React, { Component } from "react";
import axios from "axios";
//import "./Createtimetable.css";

export default class Viewusers extends Component {
  constructor(props) {
    super(props);

    this.state = {
      users: "",
    };
  }

  handlePost = (e) => {
    axios
      .get("http://localhost:4000/users", {
      })
      .then((response) => {
        this.setState({ users: response.data });
      
      }).catch(err => {
        // THROW INSERTING USER ERROR'S
        if (err){ 
       console.log(err)}

    });
      
  };

  render() {
    return (
      <div>
        <div className="row p-5 ">
          <h1>Current Teachers</h1>
          <button onClick={this.handlePost.bind(this)}>View Teachers</button>
        <div>
          <List list={this.state.users} />
        </div>
      </div>
      </div>

    );
  }
}
function List({ list }) {
  if (!list) {
    return null;
  }

  return (
    <div >
      <p >Current Teachers In IT Department are ...</p>
      <ul>
        
        {list.map((item) => (
          <Item key={item.user_id} item={item} />
          
        ))}

      </ul>

</div> 
  
  );
} 

function Item({ item }) {
  return (
    <div>
       <div  style={{  margin:"20px"}} >
       <ul 
       className="list-inline" style={{listStyle:"none"}}>
        <li id={item.user_id}>
                <i className="fa fa-user" />
                <span className="texto_grande">
                  <i className="bi bi-plus-circle mr-3" /><strong>{item.name}</strong> 
                </span>
        </li>
      </ul>
      </div>
      </div>
  );
}
